mapper.select:
SELECT m.maker, m.name model_name, m.ranking model_ranking, m.image FROM model m WHERE m.active = 1 GROUP BY maker, NAME
AND m.maker = #{maker}
ORDER BY maker, ranking

mapper.update:
UPDATE model
SET ranking = #{model_ranking}, image = #{image}
WHERE maker = #{maker} AND name= #{model_name}

mapper.select:
SELECT id model_id, maker, name model_name, lineup, active, ranking, image, displacement, car_type, janga24, janga36, janga48, tax_type, tax_rate, cal_rate_10_24, cal_rate_10_36, cal_rate_10_48, cal_rate_50_24, cal_rate_50_36, cal_rate_50_48, maintain_fee_10_24, maintain_fee_10_36, maintain_fee_10_48, maintain_fee_50_24, maintain_fee_50_36, maintain_fee_50_48 FROM model WHERE active = 1
AND maker = #{maker}
AND name=#{model_name} ORDER BY ranking

SELECT id option_id, name, price, exclusive exc FROM options o 
WHERE active = 1 AND trim_id = #{trim_id}
ORDER BY ranking

SELECT count(*) 
FROM bank_account 
WHERE reg_id=#{reg_id}

SELECT bank_name, account, carno, user_name, reg_id, memo, used_code, recv_date 
FROM bank_account 
WHERE reg_id=#{reg_id}
ORDER BY recv_date DESC
LIMIT #{page}, #{count}

